package com.fpt.excercise2.model;

public class MyPoint {

	private int x;
	private int y;
	
	public MyPoint() {
		// TODO Auto-generated constructor stub
	}

	public MyPoint(int x, int y) {
		super();
		this.x = x;
		this.y = y;
	}

	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}
	
	
}
